import React from 'react';
import ConversationList from '../ConversationList';
import MessageList from '../MessageList';
import './Messenger.css';
import { w3cwebsocket as W3CWebSocket } from "websocket";

const clientSocket = new W3CWebSocket('ws://localhost:8081/actions');

export default function Messenger(props) {

    const connect = () => {
        const socket = new SockJS("http://myiphere:8081/simulator");
        stompClient = Stomp.over(socket);
        stompClient.connect({}, function (frame) {
            console.log("Connected " + frame);
            stompClient.subscribe("http://myiphere:8081/endpoint/greeting", function (greeting) {
                console.log("hi" + JSON.parse(greeting.body).content);
            });
        });
    };

    clientSocket.onopen = function(e) {
        console.log("[open] WebSocket connection established");
    };

    clientSocket.onmessage = function(event) {
        alert(`Data received from server: ${event.data}`);
    };

    clientSocket.onclose = function(event) {
        if (event.wasClean) {
            alert(`[close] Connection was closed properly, code=${event.code} cause=${event.reason}`);
        } else {

            alert('[close] Connection interrupted');
        }
    };

    clientSocket.onerror = function(error) {
        alert(`[error] ${error.message}`);
    };

    return (
      <div className="messenger">

          {/* <Toolbar
          title="Messenger"
          leftItems={[
            <ToolbarButton key="cog" icon="ion-ios-cog" />
          ]}
          rightItems={[
            <ToolbarButton key="add" icon="ion-ios-add-circle-outline" />
          ]}
        /> */}

        {/* <Toolbar
          title="Conversation Title"
          rightItems={[
            <ToolbarButton key="info" icon="ion-ios-information-circle-outline" />,
            <ToolbarButton key="video" icon="ion-ios-videocam" />,
            <ToolbarButton key="phone" icon="ion-ios-call" />
          ]}
        /> */}

        <div className="scrollable sidebar">
          <ConversationList wsClient={client}/>
        </div>

        <div className="scrollable content">
          <MessageList wsClient={client}/>
        </div>
      </div>
    );
}
