import React, {useEffect} from 'react';
import ConversationList from '../ConversationList';
import MessageList from '../MessageList';
import './Messenger.css';
import { w3cwebsocket as W3CWebSocket } from "websocket";
import * as SockJS from 'sockjs-client';
import * as Stomp from 'stompjs';

// const clientSocket = new W3CWebSocket('ws://localhost:8081');

export default function Messenger(props) {

    let url = 'http://localhost:8081';
    let options = {};
    let sockjs = new SockJS(url, /*_reserved*/null, options);
    let stompClient;

    useEffect(async () => {
        connect;
    },[])

    const connect = () => {

        let socket = new SockJS(url);
        stompClient = Stomp.over(socket);

        stompClient.connect({}, onConnected, this.onError);
    }

    const onConnected = () => {
        console.log("onConnected");
        // Subscribe to the Public Topic
        stompClient.subscribe("/topic/public", onMessageReceived);

        // Tell your username to the server
        stompClient.send(
            "/api/chat/addUser/1",
            {},
            JSON.stringify({ sender: "Ali", type: "JOIN" })
        );
    }

    const onMessageReceived = (payload) => {
        console.log("onMessageReceived");
        var message = JSON.parse(payload.body);
    }


    sockjs.onopen = function() {
        console.log('open');
        let stompClient = Stomp.over(sockjs);
        stompClient.connect({}, function( frame ){
            console.log( "Connected :- "+frame );
            stompClient.subscribe("/actions/messages", function( notifications ) {
                alert( notifications );
            });
        }, function( error ) {
            alert( error );
        });
        sockjs.send('test');
        //sock.send('test');
        sockjs.send(JSON.stringify({ name: 'Test User', message: 'This is a hard-coded test'}));
        //sockjs.close();
    };
    /*  NB: Moved into App
    sockjs.onmessage = function(e) {
        console.log('message', e.data);
    }; //*/
    sockjs.onclose = function() {
        console.log('close');
    };

    //
    // clientSocket.onopen = function(e) {
    //     console.log("[open] WebSocket connection established");
    // };
    //
    // clientSocket.onmessage = function(event) {
    //     alert(`Data received from server: ${event.data}`);
    // };
    //
    // clientSocket.onclose = function(event) {
    //     if (event.wasClean) {
    //         alert(`[close] Connection was closed properly, code=${event.code} cause=${event.reason}`);
    //     } else {
    //
    //         alert('[close] Connection interrupted');
    //     }
    // };
    //
    // clientSocket.onerror = function(error) {
    //     alert(`[error] ${error.message}`);
    // };

    return (
      <div className="messenger">

          {/* <Toolbar
          title="Messenger"
          leftItems={[
            <ToolbarButton key="cog" icon="ion-ios-cog" />
          ]}
          rightItems={[
            <ToolbarButton key="add" icon="ion-ios-add-circle-outline" />
          ]}
        /> */}

        {/* <Toolbar
          title="Conversation Title"
          rightItems={[
            <ToolbarButton key="info" icon="ion-ios-information-circle-outline" />,
            <ToolbarButton key="video" icon="ion-ios-videocam" />,
            <ToolbarButton key="phone" icon="ion-ios-call" />
          ]}
        /> */}

        <div className="scrollable sidebar">
          <ConversationList wsClient={sockjs}/>
        </div>

        <div className="scrollable content">
          <MessageList wsClient={sockjs}/>
        </div>
      </div>
    );
}
