import React, {useState, useEffect} from 'react';

// import './AddChat.css';
import Toolbar from "../Toolbar";
import ToolbarButton from "../ToolbarButton";
import ConversationSearch from "../ConversationSearch";
import ConversationListItem from "../ConversationListItem";
import ToggleButton from "./ToggleButton";

export default function AddChat(props) {

    const [isGroup, setIsChat] = useState(true);

    useEffect(() => {
        getConversations()
    }, []);

    return (
        <div className="add-chat">
            <ToggleButton/>
            <input
                type="search"
                className="conversation-search-input"
                placeholder={isGroup ? "Your group name" : "Username"}
            />

            <Toolbar
                title="Messenger"
                leftItems={[
                    <ToolbarButton key="cog" onClick={() => console.log("properties open")} icon="ion-ios-cog"/>
                ]}
                rightItems={[
                    <ToolbarButton key="add" onClick={() => createGroup()} icon="ion-ios-add-circle-outline"/>
                ]}
            />
            <ConversationSearch onclick={() => console.log("search")}/>
            {
                conversations.map(conversation =>
                    <ConversationListItem
                        key={conversation.name}
                        data={conversation}
                    />
                )
            }
        </div>
    );
}
