import React, {useEffect} from 'react';
import ConversationList from '../ConversationList';
import MessageList from '../MessageList';
import './Messenger.css';
import { w3cwebsocket as W3CWebSocket } from "websocket";
import * as SockJS from 'sockjs-client';
import webstomp from 'webstomp-client';
import { Client } from '@stomp/stompjs';
// const clientSocket = new W3CWebSocket('ws://localhost:8081');

export default function Messenger(props) {

    let url = 'ws://localhost:8081/stomp';
    let options = {};
    let sockjs = new SockJS(url, /*_reserved*/null, options);
    let clientStomp;

    useEffect(async () => {
        connect();
    },[])

    const connect = () => {

        // let socket = new SockJS(url);
        // clientStomp = webstomp.over(new SockJS(url, {heartbeat: false}));
        //
        // clientStomp.connect({}, onConnected, console.log("oouuups"));
        let client = new Client();

        client.configure({
            brokerURL: 'http://localhost:8081',
            onConnect: () => {
                console.log('onConnect');

                this.client.subscribe('/topic/public', message => {
                    console.log("from subscription");
                    console.log(message);
                });

            },
            // Helps during debugging, remove in production
            debug: (str) => {
                console.log(new Date(), str);
            }
        });

        client.activate();
    }

    const onConnected = () => {
        console.log("onConnected");
        // Subscribe to the Public Topic
        clientStomp.subscribe("/topic/public", () => {
            console.log('received from topic public');
        });
        // clientStomp.subscribe("/topic/messages", onMessagesReceived);
        // clientStomp.subscribe("/topic/chats", onChatsReceived);
        // clientStomp.subscribe("/topic/users", onUsersReceived);

        // Tell your username to the server
        clientStomp.send(
            "/actions/test",
            {},
            //
            "init"
        );

        // clientStomp.send(
        //     "/actions/allUsers",
        //     {},
        //     JSON.stringify({ sender: "Ali", type: "JOIN" })
        // );
        //
        // clientStomp.send(
        //     "/actions/userChats",
        //     {},
        //     JSON.stringify({ sender: "Ali", type: "JOIN" })
        // );

        // clientStomp.send(
        //     "/actions/userChats",
        //     {},
        //     JSON.stringify({ sender: "Ali", type: "JOIN" })
        // );
    }

    const onTestReceived = (payload) => {
        console.log("test received");
        var message = JSON.parse(payload.body);
        console.log(message);
    }

    const onMessagesReceived = (payload) => {
        console.log("messages received");
        var message = JSON.parse(payload.body);
        console.log(message);
    }

    const onChatsReceived = (payload) => {
        console.log("chats received");
        var message = JSON.parse(payload.body);
        console.log(message);
    }

    const onUsersReceived = (payload) => {
        console.log("users received");
        var message = JSON.parse(payload.body);
        console.log(message);
    }


    /*  NB: Moved into App
    sockjs.onmessage = function(e) {
        console.log('message', e.data);
    }; //*/
    sockjs.onclose = function() {
        console.log('close');
    };

    //
    // clientSocket.onopen = function(e) {
    //     console.log("[open] WebSocket connection established");
    // };
    //
    // clientSocket.onmessage = function(event) {
    //     alert(`Data received from server: ${event.data}`);
    // };
    //
    // clientSocket.onclose = function(event) {
    //     if (event.wasClean) {
    //         alert(`[close] Connection was closed properly, code=${event.code} cause=${event.reason}`);
    //     } else {
    //
    //         alert('[close] Connection interrupted');
    //     }
    // };
    //
    // clientSocket.onerror = function(error) {
    //     alert(`[error] ${error.message}`);
    // };

    return (
      <div className="messenger">

          {/* <Toolbar
          title="Messenger"
          leftItems={[
            <ToolbarButton key="cog" icon="ion-ios-cog" />
          ]}
          rightItems={[
            <ToolbarButton key="add" icon="ion-ios-add-circle-outline" />
          ]}
        /> */}

        {/* <Toolbar
          title="Conversation Title"
          rightItems={[
            <ToolbarButton key="info" icon="ion-ios-information-circle-outline" />,
            <ToolbarButton key="video" icon="ion-ios-videocam" />,
            <ToolbarButton key="phone" icon="ion-ios-call" />
          ]}
        /> */}

        <div className="scrollable sidebar">
          <ConversationList wsClient={sockjs}/>
        </div>

        <div className="scrollable content">
          <MessageList wsClient={sockjs}/>
        </div>
      </div>
    );
}
