import React, {useState, useEffect} from 'react';
import ConversationSearch from '../ConversationSearch';
import ConversationListItem from '../ConversationListItem';
import Toolbar from '../Toolbar';
import ToolbarButton from '../ToolbarButton';
import axios from 'axios';

import './ConversationList.css';

export default function ConversationList(props) {
  const [conversations, setConversations] = useState([]);
  useEffect(() => {
    getConversations()
  },[])

 const getConversations = () => {
    axios.get('https://randomuser.me/api/?results=20').then(response => {
        let newConversations = response.data.results.map(result => {
          return {
            photo: result.picture.large,
            name: `${result.name.first} ${result.name.last}`,
            text: 'Hello world! This is a long message that needs to be truncated.'
          };
        });
        setConversations([...conversations, ...newConversations])
    });
  }

  const createGroup = () => {
      axios.post('')
  }

    return (
      <div className="conversation-list" >
        <Toolbar
          title="Messenger"
          leftItems={[
            <ToolbarButton key="cog" onClick={() => console.log("properties open")} icon="ion-ios-cog" />
          ]}
          rightItems={[
            <ToolbarButton key="add" onClick={() => console.log("add a new conversation!")}  icon="ion-ios-add-circle-outline" />
          ]}
        />
        <ConversationSearch onclick={() => console.log("add a new conversation!")}  />
        {
          conversations.map(conversation =>
            <ConversationListItem
              key={conversation.name}
              data={conversation}
            />
          )
        }
      </div>
    );
}
